mapper.select:
SELECT * FROM`order_view`

    and id= #{arg0} 
    and storeId= #{arg1} 
    and custName like CONCAT('%',#{arg2},'%') 
    and code= #{arg3} 
    and state= #{arg4} 
    and phone= #{arg5}

SELECT * FROM`order_view` where store_id=#{arg0} and state=#{arg1} and createtime between #{arg2} and #{arg3}
order by createtime ASC
order by createtime DESC
